############################################################################################
#	TRATAMOS A URL PARA PEGAR O PATH DE INSTALAÇÃO ANTES DE QUALQUER COISA				   #
############################################################################################
if(
	(isset($_SERVER['REQUEST_URI'])		&& $_SERVER['REQUEST_URI']=="/") 
){
	$WS_PATH = "/";

}else{

	$full_path_admin 	= array_filter(explode("admin",$_SERVER['SCRIPT_FILENAME']));
	$URL_1 				= array_filter(explode("/",$_SERVER['REQUEST_URI']));
	$URL_2 				= explode($URL_1[1], $full_path_admin[0]);
	$URL_3 				= str_replace(basename(__FILE__),"",$URL_1[1].$URL_2[1]);
	$WS_PATH 			= '/'.implode(array_filter(explode("/",$URL_3)),"/").'/';
}

if(!file_exists(dirname(__FILE__).'/ws-config.php')){
	$frase 	= "Ops, cadê o ws-config.php?";
	$botao 	= "Criar arquivo";
}else{
	$frase 	= "Os paths de acesso estão incorretos.<br>Para ajustar, basta acessar o painel.";
	$botao 	= "Configurar Paths";
}

$link 	= $WS_PATH."admin";

############################################################################################
?>
