			<img style="float:left;position:relative;left:50%;transform:translateX(-50%);width: 50%;" src="data:image/svg+xml;base64," >
			<span style="font-family:'Titillium Web';font-weight:100;float:left;text-align:center;width:100%;font-size:1.5em;color:#00939d;padding-top:30px;"><?=$frase?></span>	
			<a href="<?=$link;?>" style="text-decoration:none;cursor: pointer;position: relative;float: left;background-color: #7fbdc1;padding: 10px 0px;margin: 20px 0;left: 50%;transform: translateX(-50%);text-align: center;color: #FFF;font-family: 'Titillium Web';font-weight: 100;-webkit-border-radius: 5px;-moz-border-radius: 5px;border-radius: 5px;width: inherit;">
				<?=$botao?>
			</a>
		</div>
	</body>
